import React from "react";
import classes from './LocationSec.module.css';

const LocationSec = (props) => {
    
    // const table = (
    //     <table>
    //         {Object.entries(props.timetable).map( 
    //             ([day, time]) => (
    //                 <tr key={day}>
    //                     <td>{day}</td>
    //                     <td className={classes.Time}>{time}</td>
    //                 </tr>
    //             )
    //         )}
    //     </table>
        
    // );

    const currentDate = new Date();
    const currentDayIndex = currentDate.getDay();
    const [day, time] = (Object.entries(props.timetable))[currentDayIndex - 1];
    
    
    const setRow = () => {
        if(props.location===undefined){
            <tr>
                <td>Use Phone/Laptop for Video Call</td>
            </tr>
            
        }else{
            <tr>
                <td className={classes.Light}>Location: </td>
                <td>{props.location}</td>
            </tr>
        }
    }
   

    const table = (
        <table className={classes.Table}>
            
            

            <tr>
                <td className={classes.Light} >Fees:</td>
                <td>{props.fees}</td>
            </tr>

            <tr>
                <td>{day}</td>
                <td>{time}</td>
            </tr>
        </table>
    );

   
    return (
        <div className={classes.LocationSec}>
            
            <h1>{props.name}</h1>
            <p>{} </p>
        {/* <div className={classes.Table}>{table}</div> */}
            <p>{table}</p>        
        </div>
    );
}

export default LocationSec;
