import React, {useContext} from "react";
import context from "../../../Context/Context";
import ReviewPost from "./ReviewPost/ReviewPost";
import classes from './Review.module.css';

const Review = (props) => {
    const {userReview} = useContext(context);

    return (
        <div id='review-section'>
            <h1 className={classes.Heading}>Reviews About {props.name} <span style={{color: 'rgb(119, 115, 115)', fontWeight: '500'}}>({props.noOfReviews})</span> </h1>

            {
                userReview.reviews.map((ele) => <ReviewPost name={ele.name} timePassed={ele.timePassed} review={ele.review}  />
                )
            }
        </div>
        
        
    );
}

export default Review;
